import { Select } from "@chakra-ui/react";
import { changeLanguage, loadLanguages } from "i18next";
import { useTranslation } from "react-i18next";

const Header = () => {
  const [t, i18n] = useTranslation("global");
  const handlechangealanguage = (changeLanguage: String) => {
    i18n.changeLanguage(changeLanguage);
  };
  return (
    <div>
      <h1>{t("header.message")}</h1>
      <Select placeholder="Select option">
        <option value="option1">English</option>
        <option value="option2">Urdu</option>
        <option value="option3">Hindi</option>
      </Select>
    </div>
  );
};

export default Header;
