import { useTranslation } from "react-i18next";

const Header = () => {
  const [t, i18n] = useTranslation();
  const handlechangealanguage = ( String) =>{
    i18n.changeLanguage()
  }
  return (
    <div>
      <h1>{t("header.message")}</h1>
      <button onClick={() =>handlechangealanguage("EN")}>EN</button>
      <button onClick={() =>handlechangealanguage("es")}>ES</button>

    </div>
  );
};

export default Header;
